-module(cache_cli).
-export([main/1]).

%%--------------------------------------------------------------------
%%
%%--------------------------------------------------------------------
main(["serve"]) ->
    serve();
main(["add", Key, Value]) ->
    add_key(Key, Value);
main(["del", Key]) ->
    del_key(Key);
main(["list"]) ->
    list_keys();
main(["get", Key]) ->
    get_key(Key);
main(_) ->
    usage().

%%--------------------------------------------------------------------
%%
%%--------------------------------------------------------------------
usage() ->
    [ScriptName|_] = init:get_plain_arguments(),    
    io:format("Usage: ~s [serve|add|del|list|get]~n", [ScriptName]).

%%--------------------------------------------------------------------
%%
%%--------------------------------------------------------------------
serve() ->
    io:format("start cache service...~n").

%%--------------------------------------------------------------------
%%
%%--------------------------------------------------------------------
get_key(Key) ->
    ok.

%%--------------------------------------------------------------------
%%
%%--------------------------------------------------------------------
del_key(Key) ->
    ok.

%%--------------------------------------------------------------------
%%
%%--------------------------------------------------------------------
list_keys() ->
    ok.

%%--------------------------------------------------------------------
%%
%%--------------------------------------------------------------------
add_key(Key, Value) ->
    ok.
